mapper.select:
SELECT t1.timecard_category_pk, count(*) AS ATTENDANCE_COUNT FROM ( SELECT * FROM TL_C_COMPANY_TIMECARD tcct INNER JOIN TL_C_STUDENT_INTERN tcsi ON tcct.STUDENT_INTERN_PK = tcsi.STUDENT_INTERN_PK INNER JOIN TL_JIC_STUDENT_INFO tjsi ON tcsi.STUDENT_PK = tjsi.STUDENT_PK INNER JOIN TL_C_INTERNSHIP_COURSE tcic ON TCSI.INTERNSHIP_COURSE_PK = tcic.INTERNSHIP_COURSE_PK WHERE tjsi.STUDENT_PK = #{student_pk}
AND TO_CHAR(SYSDATE, 'yy/MM/dd') >= TO_CHAR(tcic.INTERNSHIP_START_DATE, 'yy/MM/dd') AND TO_CHAR(SYSDATE, 'yy/MM/dd') <= TO_CHAR(tcic.INTERNSHIP_END_DATE, 'yy/MM/dd')
)t1 GROUP BY t1.timecard_category_pk ORDER BY t1.timecard_category_pk asc
