mapper.select:
SELECT * FROM TL_D_JOB_POSTING tdjp
WHERE tdjp.com_pk = #{com_pk}
ORDER BY tdjp.CREATED_AT DESC

mapper.delete:
DELETE FROM TL_D_INTEREST_POSTING 
WHERE job_posting_pk = #{job_posting_pk}
AND student_pk = #{student_pk}

mapper.select:
SELECT count(*) FROM TL_D_INTEREST_POSTING 
WHERE job_posting_pk = #{job_posting_pk}
AND student_pk = #{student_pk}

SELECT tdjp.* FROM TL_D_JOB_POSTING tdjp WHERE tdjp.COM_PK = #{com_pk} AND tdjp.JOB_POSTING_PK IN ( SELECT t2.JOB_POSTING_PK FROM( SELECT t1.JOB_POSTING_PK FROM ( SELECT tdv.job_posting_pk FROM TL_D_VOLUNTEER tdv GROUP BY tdv.JOB_POSTING_PK ORDER BY count(*) DESC )t1
WHERE ROWNUM <= 4
)t2 ) ORDER BY tdjp.POSTING_DEADLINE ASC

SELECT * FROM ( SELECT tjsi.* FROM TL_JIC_STUDENT_INFO tjsi WHERE tjsi.STUDENT_PK IN ( SELECT tdic.STUDENT_PK FROM TL_D_INTEREST_COMPANY tdic WHERE tdic.COM_PK = #{com_pk} )
AND ROWNUM <= 4
) t1

SELECT *
FROM (
    SELECT tjsi.*
    FROM TL_JIC_STUDENT_INFO tjsi
    WHERE tjsi.STUDENT_PK IN (
        SELECT tdic.STUDENT_PK
        FROM TL_D_INTEREST_COMPANY tdic
        WHERE tdic.COM_PK = #{com_pk} 
	)
)
