mapper.update:
UPDATE cp_notice 
SET read_count = read_count + 1
WHERE id = #{id}

UPDATE cp_notice 
SET title = #{title}, text = #{text}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM cp_notice_like 
WHERE notice_id = #{notice_id} 
AND student_pk = #{student_pk}

SELECT count(*) FROM cp_notice_like
WHERE notice_id = #{notice_id} 
AND student_pk = #{student_pk} 
AND notice_like = 1

SELECT count(*) FROM cp_notice_like
WHERE notice_id = #{notice_id} 
AND student_pk = #{student_pk} 
AND notice_dislike = 1

mapper.delete:
DELETE FROM cp_notice_like
WHERE notice_id = #{notice_id} 
AND student_pk = #{student_pk}
AND notice_like = 1

DELETE FROM cp_notice_like
WHERE notice_id = #{notice_id} 
AND student_pk = #{student_pk}
AND notice_dislike = 1

mapper.select:
SELECT COUNT(*) FROM cp_notice_like
WHERE NOTICE_ID = #{notice_id} 
AND notice_like = 1

SELECT COUNT(*) FROM cp_notice_like
WHERE notice_id = #{notice_id} 
AND notice_dislike = 1
